        return new Source("<div class=\"container\">
    <h1>Produits pour la catégorie : {{ category.name }}</h1>
    <div class=\"row\">
        {% for product in products %}
            <div class=\"col-md-4\">
                <div class=\"card\">
                    <img src=\"{{ asset('uploads/images/' ~ product.image) }}\" class=\"card-img-top\" alt=\"{{ product.name }}\">
                    <div class=\"card-body\">
                        <h5>{{ product.name }}</h5>
                        <p>{{ product.price }} €</p>
                        <a href=\"{{ path('product_show', { id: product.id }) }}\" class=\"btn btn-info\">Détails</a>
                    </div>
                </div>
            </div>
        {% endfor %}
    </div>
</div>
